import React , { useContext } from "react";
import { Container , Navbar as NavbarComp , Nav } from "react-bootstrap";
import { Link , useHistory } from "react-router-dom";
import { userContext } from "../../context/userContext";
import booksLogo from "../../assets/books-logo.png"
import Login from "../modal/Login"
import Register from "../modal/Register"


function Navbar(props) {
  const [dispatch] = useContext(userContext);
  let history = useHistory();

  const login = () => {
    dispatch({
      type: "LOGIN",
    });
    history.push("/auth");
  };

  const register = () => {
    dispatch({
      type: "REGISTER",
    });
    history.push("/register");
  };

  return (
    <NavbarComp expand="lg">
      <Container>
        <NavbarComp.Brand as={Link} to="/">
          <img
            src={booksLogo}
            className="img-fluid rounded rounded-5"
            style={{ width: "60px", height: "60px" }}
            alt="brand"
          />
        </NavbarComp.Brand>
        <NavbarComp.Toggle aria-controls="basic-navbar-nav" />
        <NavbarComp.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto rounded rounded-5">
            <Nav.Link onClick={Login} className="btn btn-dark text-navbar text-light">
              Login
            </Nav.Link>
            <Nav.Link onClick={Register} className="btn btn-dark text-navbar text-light">
              Register
            </Nav.Link>
          </Nav>
        </NavbarComp.Collapse>
      </Container>
    </NavbarComp>
  );
}

export default Navbar;
