import { useState } from "react";
import { Modal, Button, Col, Form } from "react-bootstrap";
import * as Icon from "react-bootstrap-icons";
import Register from "../modal/Register";

function Login({ show, handleClose,  handleLogin}) {
  const [open, setOpen] = useState(false);
  const [showRegister, setShowRegister] = useState(false);


  function handleSubmit(e) {
    e.preventDefault();
  }

  function switchRegister() {
    handleClose();
    console.log(open);
    setOpen(true);
    console.log(open);
    setShowRegister(true)
  }

  return (
    <Modal show={show} centered>
      <Modal.Body className="text-dark">
        <div className="justify-content-center">
          <div className="card-auth p-4">
            <div
              className="d-flex justify-content-end fs-3 fa-2x"
              aria-hidden="true"
              onClick={handleClose}
            >
              <Icon.XLg />
            </div>
            <div className="mb-3 f-1 fs-2 fw-bold">Login</div>

            <Form onSubmit={handleSubmit}>
              <div>
                <input
                  autoFocus
                  type="email"
                  placeholder="Email"
                  onChange=""
                  name="email"
                  className="form-control py-2 mt-4 f-2"
                />
              </div>
              <div>
                <input
                  autoFocus
                  type="password"
                  placeholder="Password"
                  onChange=""
                  name="password"
                  className="form-control py-2 mt-4 f-2"
                />
              </div>
            </Form>

            <div className="d-grid gap-2 mt-5 pb-3">
              <Button
                className="btn btn-dark f-2 fw-bold"
                onClick={handleLogin}
              >
                Login
              </Button>
            </div>
            <div>
              <p className="f-2">
                Don't have an account
                <Button onClick={switchRegister}>Register</Button>
                {open && (
                <Register open={open} handleClose={handleClose} show={showRegister}/>
              )}
              </p>

            </div>
          </div>
        </div>
      </Modal.Body>
    </Modal>
  );
}

export default Login;
